def send_multicall():
    calldatas = []
    for index in range(h*h):
        calldata = pp.encodeABI(fn_name="lastPurchaseTimeOfTokenId", args=[index])
        calldatas.append((contract_addr, calldata))

    responses = multicall_contract.functions.tryAggregate(False, calldatas).call()

    minted = []
    for x in range(len(responses)):
        if int(responses[x][1].hex(), 16) == 0:
            continue
        minted.append(x)

    calldatas = []
    for index in minted:
        calldata = pp.encodeABI(fn_name="tokenIdsPixelColor", args=[index])
        calldatas.append((contract_addr, calldata))

    color_responses = multicall_contract.functions.tryAggregate(False, calldatas).call()
    #print('Minted!')
    return color_responses, minted


def parse_rgb(color_responses, minted):
    for x in range(len(color_responses)):
        rgb = color_responses[x][1].hex()
        r = int(rgb[:64], 16)
        g = int(rgb[64:128], 16)
        b = int(rgb[128:], 16)
        token_color_map[minted[x]] = {
            'r': int(rgb[:64], 16),
            'g': int(rgb[64:128], 16),
            'b': int(rgb[128:], 16)
        }
    database.dbSaveData({ 'pixels': json.dumps(token_color_map, separators=(',', ':')) })
    return token_color_map


